import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Title(props){
    return (
        <h1>
            {props.value}
        </h1>
    );
}

function Task(props){
    return(
      <h3>
          {props.value}
      </h3>  
    );
}

function Assignee(props){
    return(
        <h3>
            {props.value}
        </h3>
    );
}

class TaskCard extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            title:"Order Lunch",
            task:"Order fries",
            assignee:"John Doe"
        }
    }

    renderTitle(title){
        return(
            <Title
                value = {title}
            />
        );
    }

    renderTask(task){
        return(
            <Task
                value = {task}
            />
        );
    }

    renderAssignee(assignee){
        return(
            <Assignee
                value = {assignee}
                />
        );
    }

    render(){
        const title = this.state.title;
        const task = this.state.task;
        const assignee = this.state.assignee;

        return(
            <div class = "taskcard">
                <div class = "title">
                    {this.renderTitle(title)}
                </div>
                <div class = "sticky-content">

                </div>
                <div class = "task-footer">

                    <div class = "task">
                        {this.renderTask(task)}
                    </div>
                    <div class = "assignee">
                        {this.renderAssignee(assignee)}
                    </div>
                </div>
            </div>
        );
    }
}
  // ========================================

  ReactDOM.render(
    <TaskCard />,
    document.getElementById('root')
  );
